#!/usr/bin/python\n\nimport smtplib\nfrom email.mime.text import MIMEText\n\n# Email content and headers\nmsg = MIMEText('Hello world.')\nmsg['Subject'] = 'Test Subject'\nmsg['From'] = 'myname@gmail.com'\nmsg['To'] = 'somename@somewhere.com'\n\n# Setup the server connection and send email\nserver = smtplib.SMTP('smtp.gmail.com', 587)\nserver.ehlo()\nserver.starttls()\nserver.ehlo()\nserver.login('myname@gmail.com', 'mypass')\nserver.sendmail('myname@gmail.com', ['somename@somewhere.com'], msg.as_string())\nserver.close()