# Define people and their availability/experience\npeople = [\n    {"name": "Person A", "availability": ["MondayMorning", "ThursdayAfternoon"], "max_months": 4},\n    {"name": "Person B", "availability": ["MondayMorning"], "max_months": 10}\n]\n\n# Define event slots\nevent_slots = ["August1st2009_Morning", "August1st2009_Afternoon", "August8th2009_Morning", "August8th2009_Afternoon"]\n\n# Construct cost matrix\nimport numpy as np\n\ncost_matrix = []\nhigh_cost = 1000\n\nfor person in people:\n    row = []\n    for slot in event_slots:\n        day, time_of_day = slot.split('_')\n        if f"{day}{time_of_day.capitalize()}" in person["availability"] and slot.count(day + "_") <= person["max_months"]:\n            # Assign a lower cost based on experience, here we assume 10 points per month\n            row.append(10 / person["max_months"])\n        else:\n            row.append(high_cost)\n    cost_matrix.append(row)\n\ncost_matrix = np.array(cost_matrix)\nprint("Cost Matrix:")\nprint(cost_matrix)