# Python script to convert CSV from ISO-8859-1 to UTF-8\nimport csv\n\ndef convert_csv_encoding(input_filename, output_filename):\n    with open(input_filename, 'r', encoding='iso-8859-1') as infile:\n        reader = csv.reader(infile)\n        data = [row for row in reader]\n\n    with open(output_filename, 'w', encoding='utf-8', newline='') as outfile:\n        writer = csv.writer(outfile)\n        writer.writerows(data)\n\nconvert_csv_encoding('input.csv', 'output.csv')