# Define symbolic names for ORA error codes\nORA_ERROR_CODES = {\n    25401: "CANNOT_CONTINUE_FETCHES",\n    25402: "TRANSACTION_MUST_ROLLBACK",\n    25408: "CANNOT_SAFELY_REPLAY_CALL"\n}\n\n# Define logical groupings based on error behavior\nERROR_GROUPINGS = {\n    "NODE_DOWN_RETRY": (25401, 25402, 25408)\n}\n\ndef handle_database_error(e):\n    error_code = e.message.code\n    if ORA_ERROR_CODES.get(error_code) in ERROR_GROUPINGS["NODE_DOWN_RETRY"]:\n        print('node going down, restarting transaction...')\n        conn.rollback()\n        return True  # Indicate that the error was handled and can be retried\n    else:\n        raise e\n\n# Usage\ntry:\n    # some database code...\nexcept cx_Oracle.DatabaseError as e:\n    if not handle_database_error(e):\n        raise