# Example of a simple Python script to check if a file is a .doc and alert for potential risks\nimport os\n\ndef check_doc_file_risks(file_path):\n    # Basic check for file extension\n    if os.path.splitext(file_path)[1].lower() == '.doc':\n        print("Alert: The file might contain macros or other executable content. Proceed with caution!")\n    else:\n        print("File is not a .doc, no specific macro risk.")\n\n# Usage\nfile_to_check = 'report.doc'\ncheck_doc_file_risks(file_to_check)