# urls.py\nfrom django.conf.urls import url\nfrom django.views.generic import DetailView, ListView\nfrom polls.models import Poll\n\n\ninfo_dict = {\n    'queryset': Poll.objects.all(),\n}\n\nurlpatterns = [\n    url(r'^$', ListView.as_view(**info_dict), name='poll_index'),\n    url(r'^(?P<pk>\d+)/$', DetailView.as_view(**info_dict), name='poll_detail'),\n    url(r'^(?P<pk>\d+)/results/$', DetailView.as_view(\n        model=Poll,\n        template_name='polls/results.html'\n    ), name='poll_results'),\n    url(r'^(?P<poll_id>\d+)/vote/$', 'djtest.polls.views.vote', name='poll_vote'),\n]