# Model/database.py\n\nimport importlib\n\nclass Database:\n    _models = ['User']  # List of all model names that need ORM capabilities\n\n    @classmethod\n    def initialize(cls):\n        # Initialize the database connection here\n        for model_name in cls._models:\n            module = importlib.import_module(f'.{model_name}', package='Model')\n            getattr(module, model_name).initialize()\n\n# Example usage: call Database.initialize() where needed.