# Reading a file with and without a trailing newline\n\ndef read_file_content(filename):\n    with open(filename, 'r') as file:\n        content = file.readlines()\n    return content\n\n# Assuming we have two files: 'with_newline.txt' ending with a newline and 'without_newline.txt' not ending with one\ncontent_with = read_file_content('with_newline.txt')\ncontent_without = read_file_content('without_newline.txt')\n\nprint("Content with trailing newline:", content_with)\nprint("Content without trailing newline:", content_without)