# Example of server-side code using Flask\n\nfrom flask import Flask, jsonify, request\nimport sqlite3\n\napp = Flask(__name__)\n\ndef get_new_answer_count(question_id, last_checked_time):\n    conn = sqlite3.connect('stackoverflow.db')\n    cursor = conn.cursor()\n    query = "SELECT COUNT(*) FROM answers WHERE question_id=? AND created_at>?"\n    cursor.execute(query, (question_id, last_checked_time))\n    count = cursor.fetchone()[0]\n    conn.close()\n    return count\n\n@app.route('/check-new-answers/<int:question_id>')\ndef check_new_answers(question_id):\n    last_checked_time = request.args.get('lastChecked', type=int)  # Timestamp of the last check\n    new_answer_count = get_new_answer_count(question_id, last_checked_time)\n    return jsonify({'newAnswerCount': new_answer_count})\n\nif __name__ == '__main__':\n    app.run(debug=True)