# Example using the WHOISJSON API to get recent domains\n   import requests\n\n   def fetch_recent_domains():\n       url = "https://api.whoisjson.com/recent-domains"\n       response = requests.get(url)\n       if response.status_code == 200:\n           return response.json()\n       else:\n           raise Exception("Failed to fetch data")\n\n   recent_domains = fetch_recent_domains()\n   print(recent_domains)