# Function to read a file and create a dictionary with key and value\ndef read_file_to_dict(filename):\n    result = {}\n    with open(filename, 'r') as f:\n        for line in f:\n            parts = line.strip().split('\t')\n            if len(parts) == 2:\n                key, value = parts\n                result[key] = value\n    return result\n\n# Read files into dictionaries\ndict_file2 = read_file_to_dict('file2.txt')\ndict_file3 = read_file_to_dict('file3.txt')\n\n# Process the first file\nwith open('file1.txt', 'r') as file1, open('output.txt', 'w') as output:\n    for line in file1:\n        parts = line.strip().split('\t')\n        if len(parts) == 2:\n            value1, value2 = parts\n            # Lookup in dictionaries with default values as 'blank' if key not found\n            value3 = dict_file2.get(value2, 'blank')\n            value4 = dict_file3.get(value2, 'blank')\n            # Write the combined result to output file\n            output.write(f"{value1}\t{value2}\t{value3}\t{value4}\n")