#!/usr/bin/env python\n\nimport pygtk\npygtk.require('2.0')\nimport gtk\nimport gtk.glade\n\nclass TestClass:\n    def __init__(self):\n        self.wTree = gtk.glade.XML("test.glade")\n        dic = {"on_TestWindow_destroy": self.on_TestClass_destroy}\n        self.wTree.signal_autoconnect(dic)\n\n        # Ensure the window is visible\n        self.window = self.wTree.get_widget("TestWindow")\n        if self.window:\n            self.window.show_all()\n\n    def on_TestClass_destroy(self, widget, data=None):\n        print("Destroying Test Window")\n        gtk.main_quit()\n\nif __name__ == "__main__":\n    window = TestClass()\n    gtk.main()