# Assume each line in the file is a record with two fields: ID and Name\ndef read_records_from_file(file_path):\n    with open(file_path, 'r') as file:\n        for line in file:\n            # Each line represents a record that we split into fields\n            try:\n                record_id, name = line.strip().split(',', 1)\n                print(f"ID: {record_id}, Name: {name}")\n            except ValueError:\n                print("Invalid record format found")\n\n# Example usage\nread_records_from_file('example.txt')