# Example of using Kademlia, a popular DHT protocol\n   from kademlia.network import Server\n\n   async def find_peers():\n       server = Server()\n       await server.listen(5678)\n       bootstrap_node = ("192.168.0.1", 5678)  # You need at least one known node IP statically or through another means.\n       await server.bootstrap([bootstrap_node])\n       result = await server.get('some_key')\n       print(result)\n\n   import asyncio\n   asyncio.run(find_peers())