import fitz  # PyMuPDF\n\ndef extract_note_annotations(pdf_path):\n    """\n    Extracts text from note annotations in a given PDF file.\n\n    :param pdf_path: Path to the PDF file.\n    :return: List of strings, each representing the content of a note annotation.\n    """\n    notes = []\n    document = fitz.open(pdf_path)\n    for page_num in range(len(document)):\n        page = document.load_page(page_num)\n        annots = page.annots()\n        for annot in annots:\n            if annot.type[1] == "Text":  # Check if the annotation is a note\n                text = annot.info.get("content", "")\n                notes.append(text)\n    return notes\n\n# Usage example\npdf_path = 'example.pdf'\nnote_contents = extract_note_annotations(pdf_path)\nfor note in note_contents:\n    print(note)