# views.py\nfrom django.http import JsonResponse\nfrom .models import Comment\n\ndef add_comment(request):\n    if request.method == 'POST':\n        # Assuming you have a form that sends comment data via POST\n        user = request.user  # or however you get the current user\n        post_id = request.POST.get('post_id')\n        text = request.POST.get('text')\n        \n        # Create and save new Comment object\n        comment = Comment(post_id=post_id, author=user.username, text=text)\n        comment.save()\n        \n        # Return JSON response with the added comment data\n        return JsonResponse({'id': comment.id, 'username': comment.author, 'text': comment.text})\n    else:\n        return JsonResponse({'error': 'Invalid request'}, status=400)