# app.py\n\nfrom flask import Flask, render_template_string, redirect, url_for\nimport subprocess\n\napp = Flask(__name__)\n\n@app.route('/')\ndef index():\n    return render_template_string('''\n        <html>\n            <head><title>Run Scripts</title></head>\n            <body>\n                <h1>Select a Script to Run</h1>\n                <form action="/run/file_arranger" method="post">\n                    <button type="submit">Run File Arranger</button>\n                </form>\n            </body>\n        </html>\n    ''')\n\n@app.route('/run/file_arranger', methods=['POST'])\ndef run_file_arranger():\n    try:\n        result = subprocess.run(['python', 'file_arranger.py'], capture_output=True, text=True)\n        return f"Script Output:<br>{result.stdout}"\n    except Exception as e:\n        return f"Error: {str(e)}"\n\nif __name__ == '__main__':\n    app.run(host='0.0.0.0', port=5000)