# Example Python script using MySQL Connector/Python\nimport mysql.connector\n\ndef sync_databases():\n    main_db = mysql.connector.connect(\n        host="localhost",\n        user="your_user",\n        password="your_password",\n        database="main_database"\n    )\n    cursor_main = main_db.cursor()\n    \n    other_dbs = [\n        {"name": "forum", "connection": None},\n        {"name": "news", "connection": None},\n        {"name": "calendar", "connection": None}\n    ]\n    \n    for db in other_dbs:\n        db["connection"] = mysql.connector.connect(\n            host="localhost",\n            user="your_user",\n            password="your_password",\n            database=db["name"]\n        )\n    \n    # Get pending changes\n    cursor_main.execute("SELECT * FROM changelog_users WHERE sync_status = 'pending'")\n    changes = cursor_main.fetchall()\n    \n    for change in changes:\n        user_id, operation, data, status = change\n        \n        # Apply change to each other database\n        for db in other_dbs:\n            if operation == 'I':\n                insert_query = "INSERT INTO user (id, username) VALUES (%s, %s)"\n                values = (user_id, data['username'])\n                cursor = db["connection"].cursor()\n                cursor.execute(insert_query, values)\n            elif operation == 'U':\n                update_query = "UPDATE user SET username = %s WHERE id = %s"\n                values = (data['username'], user_id)\n                cursor = db["connection"].cursor()\n                cursor.execute(update_query, values)\n            elif operation == 'D':\n                delete_query = "DELETE FROM user WHERE id = %s"\n                cursor = db["connection"].cursor()\n                cursor.execute(delete_query, (user_id,))\n            \n            # Commit changes and mark as done\n            db["connection"].commit()\n        \n        # Update the main database that these changes are applied\n        update_sync_status_query = "UPDATE changelog_users SET sync_status = 'done' WHERE id = %s"\n        cursor_main.execute(update_sync_status_query, (change[0],))\n    \n    main_db.commit()\n\n# Call this function periodically or set up a cron job\nsync_databases()