# models.py\nfrom django.db import models\n\nclass Message(models.Model):\n    content = models.TextField()\n    source = models.CharField(max_length=100)\n    created_at = models.DateTimeField(auto_now_add=True)\n\n# views.py\nfrom django.core.paginator import Paginator\nfrom .models import Message\nfrom django.shortcuts import render\n\ndef message_view(request):\n    # Fetch extra messages to account for grouping\n    all_messages = list(Message.objects.all().order_by('created_at'))\n    \n    grouped_messages = []\n    i = 0\n    while i < len(all_messages):\n        msg = all_messages[i]\n        count = 1\n        while (i + count) < len(all_messages) and all_messages[i + count].source == msg.source:\n            count += 1\n        grouped_message = {'message': msg, 'count': count}\n        grouped_messages.append(grouped_message)\n        i += count\n    \n    # Paginate the grouped messages\n    paginator = Paginator(grouped_messages, 5)  # Show 5 items per page.\n    page_number = request.GET.get('page')\n    page_obj = paginator.get_page(page_number)\n\n    return render(request, 'messages/page.html', {'page_obj': page_obj})\n\n# Template rendering in page.html (simplified)\n{% for group in page_obj %}\n  <div>\n    {{ group.message.content }} from {{ group.message.source }}\n    {% if group.count > 1 %}\n      (click here to see {{ group.count|add:"-1" }} more message{{ group.count|pluralize:"s" }} from {{ group.message.source }})\n    {% endif %}\n  </div>\n{% endfor %}\n\n<div class="pagination">\n  <span class="step-links">\n    {% if page_obj.has_previous %}\n      <a href="?page=1">&laquo; first</a>\n      <a href="?page={{ page_obj.previous_page_number }}">previous</a>\n    {% endif %}\n\n    <span class="current">\n      Page {{ page_obj.number }} of {{ page_obj.paginator.num_pages }}.\n    </span>\n\n    {% if page_obj.has_next %}\n      <a href="?page={{ page_obj.next_page_number }}">next</a>\n      <a href="?page={{ page_obj.paginator.num_pages }}">last &raquo;</a>\n    {% endif %}\n  </span>\n</div>