# Import necessary libraries\nfrom phe import PaillierEncryptedNumber, paillier\n\n# Key generation\npublic_key, private_key = paillier.generate_paillier_keystore()\n\n# Encrypting data from multiple parties\ndata_points = [500, 200, 300]\nencrypted_data_points = [public_key.encrypt(x) for x in data_points]\n\n# Summing the encrypted data (homomorphically)\ntotal_encrypted_sum = sum(encrypted_data_points)\n\n# Decrypting the result\ndecrypted_total = private_key.decrypt(total_encrypted_sum)\naverage = decrypted_total / len(data_points)\n\nprint(f"Decrypted total: {decrypted_total}")\nprint(f"Average value: {average}")