# Example of simple synthetic data generation in Python\n   import random\n\n   def generate_user_data(num_records):\n       users = []\n       for i in range(num_records):\n           user_id = f"user_{i}"\n           age = random.randint(18, 65)\n           email = f"{user_id}@example.com"\n           users.append((user_id, age, email))\n       return users\n\n   test_data = generate_user_data(1000)