# Example Client in Python using socket library\n\nimport socket\n\ndef connect_to_server(server_ip, port):\n    # Create a TCP/IP socket\n    client_socket = socket.socket(socket.AF_INET, socket.SOCK_STREAM)\n    \n    try:\n        # Connect the socket to the server's IP address and port number\n        client_socket.connect((server_ip, port))\n        \n        # Send data\n        message = "Hello, Server!"\n        print("Sending:", message)\n        client_socket.sendall(message.encode())\n        \n        # Look for the response\n        amount_received = 0\n        amount_expected = len(message)\n        \n        while amount_received < amount_expected:\n            data = client_socket.recv(16)\n            amount_received += len(data)\n            print("Received:", data.decode())\n    finally:\n        print("Closing socket")\n        client_socket.close()\n\n# Connect to server, use public IP of PC 1 and the same port it's listening on\nconnect_to_server('PUBLIC_IP_OR_DDNS_HOSTNAME_OF_PC1', 10000)