# views.py\nfrom django.http import JsonResponse\nfrom django.core.paginator import Paginator\n\ndef table_data(request):\n    model_objects = YourModel.objects.all().order_by('your_field')\n    paginator = Paginator(model_objects, 25) # Show 25 objects per page.\n    page_number = request.GET.get('page', 1)\n    page_obj = paginator.get_page(page_number)\n    serialized_objects = [{'id': obj.pk, 'other_field': obj.other_field} for obj in page_obj]\n    return JsonResponse({'data': serialized_objects, 'total_pages': paginator.num_pages})\n\n# urls.py\nfrom django.urls import path\nfrom .views import table_data\n\nurlpatterns = [\n    path('table-data/', table_data, name='table-data'),\n]