# Pseudo code - Application logic example\ndef add_comment(owner_type, owner_id, comment_text):\n    if not validate_owner_exists(owner_type, owner_id):\n        raise ValueError("Owner does not exist")\n    \n    # Proceed to insert into Comments table\n\ndef validate_owner_exists(owner_type, owner_id):\n    if owner_type == 'RestaurantChain':\n        return RestaurantChains.objects.filter(ID=owner_id).exists()\n    elif owner_type == 'Restaurant':\n        return Restaurants.objects.filter(ID=owner_id).exists()\n    elif owner_type == 'MenuItem':\n        return MenuItems.objects.filter(ID=owner_id).exists()\n    else:\n        return False