# kerberos_auth.py\n\nfrom requests_kerberos import HTTPKerberosAuth, REQUIRED\nimport requests\n\ndef check_webpage(url):\n    try:\n        response = requests.get(url, auth=HTTPKerberosAuth(mutual_authentication=REQUIRED))\n        print("Status Code:", response.status_code)\n        print("Response Body:", response.text[:256])  # Print the first 256 characters of the response\n    except Exception as e:\n        print("An error occurred: ", str(e))\n\nif __name__ == "__main__":\n    url = "https://protected.example.com"  # Replace with your URL\n    check_webpage(url)