# Example pseudo-code for monitoring session rate\nfrom collections import defaultdict\n\nsession_rate = defaultdict(int)\nip_rate = defaultdict(int)\n\ndef check_access(ip, user):\n    if ip_rate[ip] > MAX_IP_RATE or session_rate[user] > MAX_SESSION_RATE:\n        log_suspicious_activity(ip, user)\n        return False  # Block access\n    else:\n        ip_rate[ip] += 1\n        session_rate[user] += 1\n        return True\n\ndef log_suspicious_activity(ip, user):\n    with open('suspicious_activities.log', 'a') as f:\n        f.write(f"Suspicious activity from IP {ip} and User {user}\n")