import sqlite3  # or another database adapter\n\ndef search_in_all_columns(db_path, table_name, search_value):\n    conn = sqlite3.connect(db_path)\n    cursor = conn.cursor()\n    \n    # Get column names\n    cursor.execute(f"PRAGMA table_info({table_name})")\n    columns = [row[1] for row in cursor.fetchall()]\n    \n    # Construct query\n    conditions = ["{} LIKE ?".format(col) for col in columns]\n    query = "SELECT * FROM {} WHERE {}".format(table_name, " OR ".join(conditions))\n    \n    # Execute query\n    search_param = "%" + search_value + "%"\n    cursor.execute(query, [search_param] * len(columns))\n    results = cursor.fetchall()\n    \n    conn.close()\n    return results\n\n# Usage\nresults = search_in_all_columns('your_database.db', 'your_table_name', 'your_search_value')\nfor row in results:\n    print(row)