# Example of opening an Alibre Design part file using Python scripting API\nfrom alibre import Part\n\ndef main():\n    # Create a new document manager\n    doc_manager = DocumentManager()\n    \n    # Open the part file\n    part_file_path = "path_to_your_part_file.AD_PRT"\n    part_doc = doc_manager.Open(part_file_path)\n    \n    if part_doc == None:\n        print("Failed to open the part file.")\n        return\n    \n    # Perform operations on the document here...\n    \n    # Save and close the document\n    doc_manager.Save(part_doc)\n    doc_manager.Close(part_doc)\n\nif __name__ == "__main__":\n    main()