import fitz  # PyMuPDF\n\ndef extract_stream_from_pdf(pdf_path, reference_id):\n    doc = fitz.open(pdf_path)\n    \n    for page_num in range(doc.page_count):\n        page = doc.load_page(page_num)\n        xref = page.get_contents()[0]  # Get the content's xref number\n        stream_xrefs = [xref]\n        \n        while stream_xrefs:\n            current_xref = stream_xrefs.pop(0)\n            obj = doc.xref_object(current_xref, compressed=True)\n            \n            if isinstance(obj, dict) and '/Properties' in obj:\n                properties = obj['/Properties']\n                \n                if isinstance(properties, dict) and '/MC0' in properties:\n                    mc0 = properties['/MC0']\n                    \n                    if isinstance(mc0, dict) and '/BLABLABLA' in mc0:\n                        blablabla_xref = mc0['/BLABLABLA']\n                        \n                        # Get the stream for BLABLABLA\n                        blablabla_stream = doc.xref_object(blablabla_xref, compressed=True)\n                        print("Stream content of BLABLABLA:", blablabla_stream)\n    \n    doc.close()\n\n# Example usage\npdf_path = 'your_document.pdf'\nextract_stream_from_pdf(pdf_path, 584)  # Assuming the reference ID for BLABLABLA is 584