import fitz  # PyMu Fitz\n\n# Open the document\ndoc = fitz.open("file.pdf")\n\n# Define XMP metadata\nxmp_metadata = """<x:xmpmeta xmlns:x="adobe:ns:metadata/1.0/"\n xmlns:xmp="http://ns.adobe.com/xap/1.0/" >\n <rdf:RDF xmlns:rdf="http://www.w3.org/1999/02/22-rdf-syntax-ns#">\n  <rdf:Description rdf:about=""\n   xmlns:dc="http://purl.org/dc/elements/1.1/"\n   dc:title="My Document"\n  />\n </rdf:RDF>\n</x:xmpmeta>"""\n\n# Set XMP metadata\ndoc.set_xmp_metadata(xmp_metadata)\n\n# Save the document with XMP metadata\ndoc.save("output_with_xmp.pdf", garbage=3, deflate=True)