#!/usr/bin/env python\n\nimport sys\nimport gtk\n\nclass MyDialog(gtk.Dialog):\n    __gtype_name__ = "MyDialog"\n\n    def __init__(self, builder):\n        super(MyDialog, self).__init__()\n        builder.add_from_file("mydialog.glade")\n        builder.connect_signals(self)\n\n        # Retrieve the TreeView widget and assign it to an instance variable\n        self.treeview = builder.get_object("treeview_id_in_glade")\n\nif __name__ == "__main__":\n    builder = gtk.Builder()\n    dialog = MyDialog(builder)\n    dialog.run()