# On the receiving end, split out the message and signature\ndelimiter = b'\n---SIGNATURE---\n'\nsplit_index = combined_file_data.find(delimiter)\nreceived_message = combined_file_data[:split_index]\nreceived_signature = combined_file_data[split_index + len(delimiter):]\n\n# Verify the signature\ntry:\n    pkcs1_15.new(key.publickey()).verify(SHA256.new(received_message), received_signature)\n    print("The signature is valid.")\nexcept (ValueError, TypeError):\n    print("The signature is not valid.")