#!/usr/local/bin/ruby\n\nrequire 'cgi'\nload 'inc_game.cgi'\n\ncgi = CGI.new\n\n# Create cookie\ncookie = CGI::Cookie.new("name" => "rubyweb", "value" => "CustID=123;Part=ABC")\n\n# Start response with cookies\nputs cgi.header("cookie" => [cookie])\nputs "Content-Type: text/html\n\n"\n\nplay = Game.new\n\n# Check if game submit buttons are pressed\ndoIt = cgi['play']\n\n# Welcome message\nif doIt == ''\n  puts play.displayGreeting\nend\n\n# Choose weapon\nplay.playGame\n\nif doIt == 'Play'\n  move = cgi['weapon']\n  human = play.humanMove(move)\n  computer = play.ComputerMove\n  print human\n  print computer\n  result = play.results(human, computer)\n  play.displayResults(result)\nend