require 'twitter'\n\n   client = Twitter::REST::Client.new do |config|\n     config.consumer_key        = ServiceAPIKeys.twitter['api_key']\n     config.consumer_secret     = ServiceAPIKeys.twitter['secret_key']\n     config.access_token        = ServiceAPIKeys.twitter['access_token']\n     config.access_token_secret = ServiceAPIKeys.twitter['access_token_secret']\n   end\n\n   # Example usage:\n   client.update("I love cats!")