require 'wordnik'\ninclude Wordnik\n\napi_key = "YOUR_API_KEY" # Replace with your actual Wordnik API key\nclient = Wordnik.client(api_key)\n\ndef get_definition(word)\n  api = WordsApi.new(client)\n  definitions = api.get_definitions(word)\n  \n  if definitions && !definitions.empty?\n    definitions.map { |d| d.text }\n  else\n    ["No definition found."]\n  end\nend\n\n# Example usage:\nputs get_definition("ruby")