require 'net/ldap'\n\nldap = Net::LDAP.new :host => "your-ldap-server.com"\nldap.authenticate("cn=admin,dc=example,dc=com", "password")\n\n# Assuming you want to fetch all users under a specific organizational unit\nfilter = Net::LDAP::Filter.eq('objectClass', 'person')\n\ntreebase = "ou=Users,dc=example,dc=com"\n\nusers = ldap.search(:base => treebase, :filter => filter)\n\nusers.each do |entry|\n  puts "User: #{entry.cn.first}"\nend