def utf8_valid?(path)\n  begin\n    File.open(path, 'r:UTF-8') do |f|\n      f.each_line do |line|\n        # Simply reading each line with UTF-8 encoding will raise an exception if invalid\n      end\n    end\n    true\n  rescue ArgumentError => e\n    puts "Invalid UTF-8: #{e}"\n    false\n  end\nend\n\n# Usage\nfile_path = 'path/to/your/file.txt'\nif utf8_valid?(file_path)\n  puts "#{file_path} is valid UTF-8"\nelse\n  puts "#{file_path} is NOT valid UTF-8"\nend