# config/routes.rb\nresources :products do\n  # Traditional resource routes\nend\n\nnamespace :ajax do\n  resources :products, only: [:search] # Non-RESTful action for product search\n  get 'global_search', to: 'generic#search' # Global search unrelated to a specific resource\nend\n\n# app/controllers/ajax/products_controller.rb\nmodule Ajax\n  class ProductsController < ApplicationController\n    def search\n      @results = Product.where('name LIKE ?', "%#{params[:query]}%")\n      respond_to do |format|\n        format.json { render json: @results }\n      end\n    end\n  end\nend\n\n# app/controllers/ajax/generic_controller.rb\nmodule Ajax\n  class GenericController < ApplicationController\n    def search\n      # Global search logic that spans multiple models\n    end\n  end\nend