# app/controllers/items_controller.rb\n   def update_status\n     @item = Item.find(params[:id])\n     if @item.update(approved: params[:approved] == "true", approved_by: current_user.id, approved_at: Time.current)\n       respond_to do |format|\n         format.js { head :ok }\n       end\n     else\n       render turbo_stream: turbo_stream.replace(@item,\n                                                 partial: 'items/item',\n                                                 locals: { item: @item })\n     end\n   end