require 'bcrypt'\n\n# Hash a password\npassword = "my_secure_password"\nhashed_password = BCrypt::Password.create(password)\n\n# Store hashed_password in your database\n\n# Verifying a stored password with the user input\nuser_input = "my_secure_password" # This comes from the login form\nif BCrypt::Password.new(hashed_password) == user_input\n  puts "Login successful!"\nelse\n  puts "Invalid credentials"\nend