class User < ApplicationRecord\n  # Ensure no duplicate validations like these two:\n  validates :email, presence: true\n  # Check if there's another line repeating this or similar validation\n\n  validate :custom_email_validation\n\n  private\n\n  def custom_email_validation\n    # Avoid adding the same error twice:\n    errors.add(:email, "already exists") unless User.exists?(email: email)\n    # If you need to check for uniqueness, consider using Rails' built-in validation:\n    # validates_uniqueness_of :email\n  end\nend