require 'nokogiri'\n\n# Sample HTML content\nhtml_content = <<-HTML\n<html>\n  <body>\n    <div id="menu">Existing Content</div>\n  </body>\n</html>\nHTML\n\n# Parse the HTML\ndoc = Nokogiri::HTML(html_content)\n\n# Select elements using CSS selectors, similar to jQuery\nmenu_element = doc.at_css('#menu')\n\n# Manipulate the element's inner HTML\nmenu_element.inner_html += ' - New Content'\n\n# Append a new element after the selected one\nnew_element = Nokogiri::XML::Node.new "div", doc\nnew_element.content = "This is a new Div"\nmenu_element.add_next_sibling(new_element)\n\nputs doc.to_html