require 'sequel'\n\n# Connect to the Database\nDB = Sequel.connect(adapter: 'postgres', host: 'localhost', dbname: 'your_database_name',\n                     user: 'your_username', password: 'your_password')\n\n# Define a migration\nSequel.migration do\n  change do\n    create_table :items do\n      primary_key :id\n      String :name, null: false\n      Integer :quantity\n\n      foreign_key :category_id, :categories, on_delete: :cascade\n    end\n  end\nend\n\n# Run the migration\nDB.extension(:migration)\nSequel.connect(DB.opts) do |db|\n  db.apply_migrations('path/to/migrations', target: nil)\nend