require 'thread'\n\nblender = IO.popen('blender -b mball.blend -o //renders/ -F JPEG -x 1 -f 1')\n\noutput_thread = Thread.new do\n  blender.each_line do |line|\n    puts line.chomp # Process each line as it's output by Blender\n    # Add logic here to update your GUI progress bar based on the line content\n  end\nend\n\nputs "Blender is doing its job now..."\n\n# Optionally, wait for the process to finish and ensure all output has been processed.\noutput_thread.join\nblender.close