require 'gtk3'\n\n# Load glade file generated from Glade\nbuilder = Gtk::Builder.new(file: "your_ui.glade")\n\n# Get references to the TreeView and ListStore\ntreeview = builder.get_object("treeview1")\nliststore = builder.get_object("liststore1")\n\n# Add columns to the TreeView\nrenderer_text = Gtk::CellRendererText.new\ncolumn_text = Gtk::TreeViewColumn.new("Items", renderer_text, { text: 0 })\ncolumn_text.sizing = :autosize\ntreeview.append_column(column_text)\n\n# Populate the ListStore with some data\nitems = ["Item 1", "Item 2", "Item 3", "Item 4"]\nitems.each do |item|\n  liststore.insert(-1, [item])\nend\n\n# Set selection mode to multiple\nselection = treeview.selection\nselection.mode = :multiple\n\n# Optionally, handle selection changes\nselection.signal_connect("changed") do\n  paths = selection.selected_rows\n  selected_items = paths.map { |path| liststore[path][0] }\n  puts "Selected items: #{selected_items.join(', ')}"\nend\n\n# Show the window and run the application\nwindow = builder.get_object("window1")\nwindow.signal_connect("destroy") { Gtk.main_quit }\nwindow.show_all\nGtk.mai