require 'open-uri'\nrequire 'nokogiri'\n\ndef get_meta_tags(url)\n  doc = Nokogiri::HTML(URI.open(url))\n  meta_tags = {}\n\n  doc.css('meta').each do |tag|\n    content = tag['content']\n    name = tag['name'] || tag['property']\n\n    next unless name && content\n\n    if meta_tags[name]\n      meta_tags[name] += ", #{content}"\n    else\n      meta_tags[name] = content\n    end\n  end\n\n  meta_tags\nend\n\n# Example usage:\nurl = 'http://example.com'\nmeta_tags = get_meta_tags(url)\nputs meta_tags