module UsefulThings\n  def get_file; "file_content"; end\n  def delete_file; "deleted"; end\n\n  def format_text(x); x.upcase; end\nend\n\nclass UsefulWorker\n  # Bind a specific method from the module to an instance of this class\n  FORMAT_TEXT_METHOD = UsefulThings.instance_method(:format_text).bind(self.new)\n\n  def do_work\n    formatted_text = FORMAT_TEXT_METHOD.call("abc")\n    puts formatted_text\n  end\nend\n\nworker = UsefulWorker.new\nworker.do_work  # Outputs: ABC