class Item\n  def self.generate_random(seed, params={})\n    srand(seed) # Seed the random number generator for reproducibility\n    Item.create(\n      name: params[:name] || ('a'..'z').to_a.sample(8).join,\n      price: params[:price] || rand(100)\n    )\n  end\nend\n\ndescribe Item do\n  describe '#most_expensive' do\n    before(:all) do\n      @items = []\n      3.times { @items << Item.generate_random(42) } # Using a fixed seed for consistency\n      @items << Item.generate_random(43, price: 50)\n    end\n\n    it 'should return the most expensive item' do\n      sorted = @items.sort { |a, b| b.price <=> a.price }\n      expensive = Item.most_expensive\n      expect(expensive).to be(sorted[0])\n      expect(expensive.price).to be >= 50      \n    end\n  end\nend