require 'httparty'\nrequire 'nokogiri' # For XML construction\n\n# Example XML construction using Nokogiri\nxml = Nokogiri::XML::Builder.new do |xml|\n  xml.root {\n    xml.methodName "gettreasurehunts"\n    xml.param1 "value1"\n    xml.resourceId "12345"\n  }\nend.to_xml\n\noptions = {\n  headers: { 'Content-Type' => 'application/xml' },\n  body: xml\n}\n\nresponse = HTTParty.post('http://site.com/gettreasurehunts', options)\n\nputs response.body