require 'net/http'\nrequire 'uri'\n\n# Construct XML request body as a string\nxml_request = <<-XML\n<root>\n  <methodName>gettreasurehunts</methodName>\n  <param1>value1</param1>\n  <resourceId>12345</resourceId>\n</root>\nXML\n\nuri = URI('http://site.com/gettreasurehunts')\nresponse = Net::HTTP.post(uri, xml_request,\n                         'Content-Type' => 'application/xml')\n\nputs response.body